import React from 'react'
import Color from './Color'

const colorInfo = [
    {
        number: 1,
        background: 'green',
        image: 'https://images.unsplash.com/photo-1472214103451-9374bd1c798e?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&w=1000&q=80'
    },{
        number: 2,
        background: 'blue'
    },{
        number: 3,
        background: 'yellow'
    },{
        number: 4,
        background: 'red'
    }
]

const ColorBox = () => {

    const mappedColors = colorInfo.map((color)=>{
        return <Color number={color.number} background={color.background} pic={color.image} />
    })
    return (
        <div id='colorContainer'>
            {mappedColors}
        </div>
    )
}

export default ColorBox
